import { Component } from '@angular/core';

@Component({
  selector: 'app-menu',
  templateUrl: './menu.component.html',
  styleUrls: ['./menu.component.css']
})
export class MenuComponent {

}
<div class="container bg-primary p-3 text-light mt-3">

  <h2 class="text-center mb-4">Menú works!</h2>

  <div class="btn-group d-grid gap-2 mx-auto">
    <button type="button" routerLink="/" class="btn btn-info btn-lg border-light text-primary"><i class="fa-solid fa-house"></i></button>
    <button type="button" routerLink="../form" class="btn btn-info btn-lg border-light text-primary"> <i class="fa-regular fa-rectangle-list"></i> </button>
    <button type="button" routerLink="#" class="btn btn-info btn-lg border-light text-primary"> <i class="fa-solid fa-circle-info"></i> </button>
  </div>

</div>


